function getFriendlyDate(n){return n.calendar(null,{sameDay:"[Today at] HH:mm",nextDay:"[Tomorrow at] HH:mm",nextWeek:"dddd [at] HH:mm",lastDay:"[Yesterday at] HH:mm",lastWeek:"[Last] dddd [at] HH:mm",sameElse:"Do MMM YYYY [at] HH:mm"})}function getFormattedDate(n){return n.format("Do MMM YYYY HH:mm:ss")}$(".auto-friendly-date").each(function(){this.innerHTML=getFriendlyDate(moment.utc(this.innerHTML).local())});$(".auto-format-date").each(function(){this.innerHTML=getFormattedDate(moment.utc(this.innerHTML).local())});$("li.auto-dropdown").hover(function(){$(this).find(".dropdown-menu").stop(!0,!0).delay(200).slideDown()},function(){$(this).find(".dropdown-menu").stop(!0,!0).delay(200).slideUp()});$(document).ready(function(){$(".auto-datatable").DataTable({paging:!1,searching:!1,ordering:!1,info:!1,responsive:!0})});